  <Router>
    <div>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/calorieCalculator">Calorie Calculator</Link>
        </li>
      </ul>

      <hr />

      <Route exact path="/" component={Home} />
      <Route path="/calorieCalculator" component={calorieCalculator} />
    </div>
  </Router>
};
*/
function App () {
  const [listOfUsers, setListOfUsers] = useState([]);
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [age, setAge] = useState("");
  //const [user, setUser] = useState("Select a User");
  /*
  let handleUserChange = (user) => {
    setUser(user.target.name)
  }
  */
  useEffect(() => {
    Axios.get('http://localhost:3001/getUsers').then((response) => {
      setListOfUsers(response.data)
    })
  }, []);

  const createUser = () => {
    Axios.post('http://localhost:3001/createUser', {name, age, username, password}).then((response) => {
      setListOfUsers([...listOfUsers, {name, age, username, password}])
    })
  }

  const navigateToCalculator = () => {
    navigate('/calorieCalculator')
  }

  return (
    <div className="App">
      <div className="userDisplay">
        <hr />
        <button onClick={navigateToCalculator}>Calorie Calculator</button>
        <Routes>
          <Route path="/calorieCalculator" element={<calorieCalculator />} />
        </Routes>
        {listOfUsers.map((user) => {
          return (
            <div>
              <h1>Name: {user.name}</h1>
              <h1>Username: {user.username}</h1>
              <h1>Age: {user.age}</h1>
            </div>
          );
        })}
        {/*
        {listOfUsers}
        <br>
        <select onChange={handleUserChange}> 
          <option name="Select a User"> -- Select a User -- </option>

          {listOfUsers.map((listOfUsers) => <option name={user.name}></option>)}
        </select>
        </br>
          */}
      </div>

      <div>
        <input type = "text" placeholder='Name...' onChange={(event) => {
          setName(event.target.value);
        }}/>
        <input type = "text" placeholder='Username...' onChange={(event) => {
          setUsername(event.target.value);
        }}/>
        <input type = "text" placeholder='Password...' onChange={(event) => {
          setPassword(event.target.value);
        }}/>
        <input type = "Number" placeholder='Age...' onChange={(event) => {
          setAge(event.target.value);
        }}/>
        <button onClick={createUser}> Create User </button>
      </div>
    </div>
  );
}

function calorieCalculator () {

  return (
    <h2>Calorie Calculator</h2>
  );
}

export default App;
